import Tinymce from '@/components/Tinymce';
import Upload from '@/components/Upload/SingleImage3';
import MDinput from '@/components/MDinput';
import Sticky from '@/components/Sticky'; // 粘性header组件

import { validURL } from '@/utils/validate';
import { fetchArticle } from '@/api/article';
import { searchUser } from '@/api/remote-search';
import Warning from "./Warning";
import { CommentDropdown, PlatformDropdown, SourceUrlDropdown } from "./Dropdown";
var defaultForm = {
  status: 'draft',
  title: '',
  // 文章题目
  content: '',
  // 文章内容
  content_short: '',
  // 文章摘要
  source_uri: '',
  // 文章外链
  image_uri: '',
  // 文章图片
  display_time: undefined,
  // 前台展示时间
  id: undefined,
  platforms: ['a-platform'],
  comment_disabled: false,
  importance: 0
};
export default {
  name: 'ArticleDetail',
  components: {
    Tinymce: Tinymce,
    MDinput: MDinput,
    Upload: Upload,
    Sticky: Sticky,
    Warning: Warning,
    CommentDropdown: CommentDropdown,
    PlatformDropdown: PlatformDropdown,
    SourceUrlDropdown: SourceUrlDropdown
  },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    var _this = this;

    var validateRequire = function validateRequire(rule, value, callback) {
      if (value === '') {
        _this.$message({
          message: rule.field + '为必传项',
          type: 'error'
        });

        callback(new Error(rule.field + '为必传项'));
      } else {
        callback();
      }
    };

    var validateSourceUri = function validateSourceUri(rule, value, callback) {
      if (value) {
        if (validURL(value)) {
          callback();
        } else {
          _this.$message({
            message: '外链url填写不正确',
            type: 'error'
          });

          callback(new Error('外链url填写不正确'));
        }
      } else {
        callback();
      }
    };

    return {
      postForm: Object.assign({}, defaultForm),
      loading: false,
      userListOptions: [],
      rules: {
        image_uri: [{
          validator: validateRequire
        }],
        title: [{
          validator: validateRequire
        }],
        content: [{
          validator: validateRequire
        }],
        source_uri: [{
          validator: validateSourceUri,
          trigger: 'blur'
        }]
      },
      tempRoute: {}
    };
  },
  computed: {
    contentShortLength: function contentShortLength() {
      return this.postForm.content_short.length;
    },
    displayTime: {
      // set and get is useful when the data
      // returned by the back end api is different from the front end
      // back end return => "2013-06-25 06:59:25"
      // front end need timestamp => 1372114765000
      get: function get() {
        return +new Date(this.postForm.display_time);
      },
      set: function set(val) {
        this.postForm.display_time = new Date(val);
      }
    }
  },
  created: function created() {
    if (this.isEdit) {
      var id = this.$route.params && this.$route.params.id;
      this.fetchData(id);
    } // Why need to make a copy of this.$route here?
    // Because if you enter this page and quickly switch tag, may be in the execution of the setTagsViewTitle function, this.$route is no longer pointing to the current page
    // https://github.com/PanJiaChen/vue-element-admin/issues/1221


    this.tempRoute = Object.assign({}, this.$route);
  },
  methods: {
    fetchData: function fetchData(id) {
      var _this2 = this;

      fetchArticle(id).then(function (response) {
        _this2.postForm = response.data; // just for test

        _this2.postForm.title += "   Article Id:".concat(_this2.postForm.id);
        _this2.postForm.content_short += "   Article Id:".concat(_this2.postForm.id); // set tagsview title

        _this2.setTagsViewTitle(); // set page title


        _this2.setPageTitle();
      }).catch(function (err) {
        console.log(err);
      });
    },
    setTagsViewTitle: function setTagsViewTitle() {
      var title = 'Edit Article';
      var route = Object.assign({}, this.tempRoute, {
        title: "".concat(title, "-").concat(this.postForm.id)
      });
      this.$store.dispatch('tagsView/updateVisitedView', route);
    },
    setPageTitle: function setPageTitle() {
      var title = 'Edit Article';
      document.title = "".concat(title, " - ").concat(this.postForm.id);
    },
    submitForm: function submitForm() {
      var _this3 = this;

      console.log(this.postForm);
      this.$refs.postForm.validate(function (valid) {
        if (valid) {
          _this3.loading = true;

          _this3.$notify({
            title: '成功',
            message: '发布文章成功',
            type: 'success',
            duration: 2000
          });

          _this3.postForm.status = 'published';
          _this3.loading = false;
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    draftForm: function draftForm() {
      if (this.postForm.content.length === 0 || this.postForm.title.length === 0) {
        this.$message({
          message: '请填写必要的标题和内容',
          type: 'warning'
        });
        return;
      }

      this.$message({
        message: '保存成功',
        type: 'success',
        showClose: true,
        duration: 1000
      });
      this.postForm.status = 'draft';
    },
    getRemoteUserList: function getRemoteUserList(query) {
      var _this4 = this;

      searchUser(query).then(function (response) {
        if (!response.data.items) return;
        _this4.userListOptions = response.data.items.map(function (v) {
          return v.name;
        });
      });
    }
  }
};
